        private static void SalvarBlob()
        {
            string storageConnection = "ConnString";

            CloudStorageAccount storageacc = CloudStorageAccount.Parse(storageConnection);

            //Create Reference to Azure Blob
            CloudBlobClient blobClient = storageacc.CreateCloudBlobClient();

            //The next 2 lines create if not exists a container named "democontainer"
            CloudBlobContainer container = blobClient.GetContainerReference("nomeContainer");

            //The next 7 lines upload the file test.txt with the name DemoBlob on the container "democontainer"
            CloudBlockBlob blockBlob = container.GetBlockBlobReference("nomedoarquivocoma extensao");

            string imgBase = @"";

            var bytes = Convert.FromBase64String(imgBase);

            blockBlob.UploadFromByteArrayAsync(bytes, 0, bytes.Length);

            Console.WriteLine("Foi!");
        }
